package com.example.zhazira_3_8_hw

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.core.os.bundleOf
import androidx.navigation.fragment.findNavController
import com.example.zhazira_3_8_hw.databinding.FragmentCharacterBinding

class CharacterFragment : Fragment() {

    private lateinit var binding: FragmentCharacterBinding

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = FragmentCharacterBinding.inflate(layoutInflater)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        initAdapter(
        )
    }

    private fun initAdapter() {
        val adapter = FilmAdapter(filmList, this::onClick)
        binding.rvFilm.adapter = adapter
    }

    private fun onClick(film: Film){
        findNavController().navigate(R.id.characterFragment, bundleOf("film" to film))
    }

    private val filmList = arrayListOf(
        Film("Garfield", "data:image/jpeg;base64,", "12 Мая 2024"),
        Film("Салт", "data:image/jpeg;base64,", "13 Мая 2024"),
        Film("Мечта с подвохом", "data:image/jpeg;base64,", "25 Июня 2024"),
        Film("Бегущий за ветром", "data:image/jpeg;base64,", "19 Июня 2024"),
    )
}